import React, { useState } from 'react';
import { 
  X, Camera, Upload, Calendar, Droplets, Sun, Thermometer, 
  CheckCircle, Clock, AlertCircle, TrendingUp, FileText 
} from 'lucide-react';

interface Plant {
  id: string;
  name: string;
  type: string;
  plantedDate: string;
  lastWatered: string;
  health: 'excellent' | 'good' | 'fair' | 'poor';
  image?: string;
}

interface PlantDetailViewProps {
  plant: Plant;
  onClose: () => void;
  onUpdatePlant: (plantId: string, updates: Partial<Plant>) => void;
}

interface DailyTask {
  id: string;
  title: string;
  description: string;
  completed: boolean;
  dueDate: string;
}

interface ProgressStage {
  id: string;
  name: string;
  description: string;
  duration: string;
  completed: boolean;
  current: boolean;
}

const PlantDetailView: React.FC<PlantDetailViewProps> = ({ plant, onClose, onUpdatePlant }) => {
  const [activeTab, setActiveTab] = useState<'progress' | 'tasks' | 'photos' | 'care'>('progress');
  const [uploadingPhoto, setUploadingPhoto] = useState(false);
  const [aiAnalysis, setAiAnalysis] = useState<string | null>(null);

  // Mock data for plant stages
  const progressStages: ProgressStage[] = [
    {
      id: '1',
      name: 'Germination',
      description: 'Seeds sprouting and developing roots',
      duration: '7-14 days',
      completed: true,
      current: false
    },
    {
      id: '2',
      name: 'Seedling',
      description: 'Young plants developing true leaves',
      duration: '14-21 days',
      completed: true,
      current: false
    },
    {
      id: '3',
      name: 'Vegetative Growth',
      description: 'Plants growing leaves and stems',
      duration: '30-60 days',
      completed: false,
      current: true
    },
    {
      id: '4',
      name: 'Flowering',
      description: 'Plants producing flowers',
      duration: '7-14 days',
      completed: false,
      current: false
    },
    {
      id: '5',
      name: 'Fruiting',
      description: 'Plants producing fruits/vegetables',
      duration: '30-90 days',
      completed: false,
      current: false
    }
  ];

  // Mock daily tasks
  const dailyTasks: DailyTask[] = [
    {
      id: '1',
      title: 'Water the plant',
      description: 'Check soil moisture and water if needed',
      completed: true,
      dueDate: new Date().toISOString()
    },
    {
      id: '2',
      title: 'Check for pests',
      description: 'Inspect leaves and stems for any signs of pests',
      completed: false,
      dueDate: new Date().toISOString()
    },
    {
      id: '3',
      title: 'Fertilize',
      description: 'Apply organic fertilizer to promote growth',
      completed: false,
      dueDate: new Date().toISOString()
    }
  ];

  const handlePhotoUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    setUploadingPhoto(true);
    
    // Simulate AI analysis
    setTimeout(() => {
      setAiAnalysis("ðŸŒ± Your plant is growing well! The leaves look healthy and green. Continue with regular watering and ensure it gets adequate sunlight. No signs of disease detected.");
      setUploadingPhoto(false);
    }, 2000);
  };

  const healthColors = {
    excellent: 'text-green-600 bg-green-100',
    good: 'text-blue-600 bg-blue-100',
    fair: 'text-yellow-600 bg-yellow-100',
    poor: 'text-red-600 bg-red-100'
  };

  const tabs = [
    { id: 'progress', name: 'Progress', icon: TrendingUp },
    { id: 'tasks', name: 'Daily Tasks', icon: CheckCircle },
    { id: 'photos', name: 'Photo Analysis', icon: Camera },
    { id: 'care', name: 'Care Guide', icon: FileText }
  ];

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-xl max-w-4xl w-full max-h-[90vh] overflow-hidden">
        {/* Header */}
        <div className="flex justify-between items-center p-6 border-b border-gray-200">
          <div>
            <h2 className="text-2xl font-bold text-gray-900">{plant.name}</h2>
            <p className="text-gray-600">{plant.type}</p>
          </div>
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600 transition-colors"
          >
            <X className="w-6 h-6" />
          </button>
        </div>

        {/* Tabs */}
        <div className="flex border-b border-gray-200">
          {tabs.map((tab) => {
            const Icon = tab.icon;
            const isActive = activeTab === tab.id;
            return (
              <button
                key={tab.id}
                onClick={() => setActiveTab(tab.id as any)}
                className={`flex items-center space-x-2 px-6 py-3 border-b-2 font-medium text-sm transition-colors ${
                  isActive
                    ? 'border-garden-green text-garden-green'
                    : 'border-transparent text-gray-500 hover:text-gray-700'
                }`}
              >
                <Icon className="w-4 h-4" />
                <span>{tab.name}</span>
              </button>
            );
          })}
        </div>

        {/* Content */}
        <div className="p-6 overflow-y-auto max-h-[60vh]">
          {activeTab === 'progress' && (
            <div className="space-y-6">
              <div className="flex items-center justify-between">
                <h3 className="text-lg font-semibold">Growth Progress</h3>
                <span className={`px-3 py-1 rounded-full text-sm font-medium ${healthColors[plant.health]}`}>
                  {plant.health} health
                </span>
              </div>

              <div className="space-y-4">
                {progressStages.map((stage, index) => (
                  <div key={stage.id} className="flex items-start space-x-4">
                    <div className={`flex-shrink-0 w-8 h-8 rounded-full flex items-center justify-center ${
                      stage.completed 
                        ? 'bg-green-500 text-white' 
                        : stage.current 
                        ? 'bg-garden-green text-white' 
                        : 'bg-gray-200 text-gray-500'
                    }`}>
                      {stage.completed ? (
                        <CheckCircle className="w-5 h-5" />
                      ) : (
                        <span className="text-sm font-medium">{index + 1}</span>
                      )}
                    </div>
                    <div className="flex-1">
                      <div className="flex items-center space-x-2">
                        <h4 className="font-medium text-gray-900">{stage.name}</h4>
                        {stage.current && (
                          <span className="px-2 py-1 bg-garden-green text-white text-xs rounded-full">
                            Current
                          </span>
                        )}
                      </div>
                      <p className="text-gray-600 text-sm mt-1">{stage.description}</p>
                      <p className="text-gray-500 text-xs mt-1">Duration: {stage.duration}</p>
                    </div>
                  </div>
                ))}
              </div>

              <div className="bg-blue-50 p-4 rounded-lg">
                <div className="flex items-start space-x-3">
                  <Clock className="w-5 h-5 text-blue-600 mt-0.5" />
                  <div>
                    <h4 className="font-medium text-blue-900">Estimated Harvest Time</h4>
                    <p className="text-blue-700 text-sm mt-1">
                      Based on your planting date ({new Date(plant.plantedDate).toLocaleDateString()}), 
                      you can expect to harvest in approximately 60-90 days.
                    </p>
                  </div>
                </div>
              </div>
            </div>
          )}

          {activeTab === 'tasks' && (
            <div className="space-y-4">
              <h3 className="text-lg font-semibold">Today's Tasks</h3>
              {dailyTasks.map((task) => (
                <div key={task.id} className="flex items-start space-x-3 p-4 border border-gray-200 rounded-lg">
                  <button
                    onClick={() => {
                      // Toggle task completion
                    }}
                    className={`flex-shrink-0 w-5 h-5 rounded border-2 flex items-center justify-center ${
                      task.completed 
                        ? 'bg-garden-green border-garden-green' 
                        : 'border-gray-300'
                    }`}
                  >
                    {task.completed && <CheckCircle className="w-3 h-3 text-white" />}
                  </button>
                  <div className="flex-1">
                    <h4 className={`font-medium ${task.completed ? 'text-gray-500 line-through' : 'text-gray-900'}`}>
                      {task.title}
                    </h4>
                    <p className={`text-sm ${task.completed ? 'text-gray-400' : 'text-gray-600'}`}>
                      {task.description}
                    </p>
                  </div>
                </div>
              ))}
            </div>
          )}

          {activeTab === 'photos' && (
            <div className="space-y-6">
              <div className="flex items-center justify-between">
                <h3 className="text-lg font-semibold">Photo Analysis</h3>
                <label className="bg-garden-green hover:bg-garden-dark text-white px-4 py-2 rounded-lg cursor-pointer transition-colors flex items-center space-x-2">
                  <Camera className="w-4 h-4" />
                  <span>Upload Photo</span>
                  <input
                    type="file"
                    accept="image/*"
                    onChange={handlePhotoUpload}
                    className="hidden"
                  />
                </label>
              </div>

              {uploadingPhoto && (
                <div className="text-center py-8">
                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-garden-green mx-auto mb-4"></div>
                  <p className="text-gray-600">Analyzing your plant with AI...</p>
                </div>
              )}

              {aiAnalysis && (
                <div className="bg-green-50 p-4 rounded-lg">
                  <div className="flex items-start space-x-3">
                    <CheckCircle className="w-5 h-5 text-green-600 mt-0.5" />
                    <div>
                      <h4 className="font-medium text-green-900">AI Analysis Result</h4>
                      <p className="text-green-700 text-sm mt-1">{aiAnalysis}</p>
                    </div>
                  </div>
                </div>
              )}

              <div className="bg-blue-50 p-4 rounded-lg">
                <div className="flex items-start space-x-3">
                  <AlertCircle className="w-5 h-5 text-blue-600 mt-0.5" />
                  <div>
                    <h4 className="font-medium text-blue-900">Photo Analysis Tips</h4>
                    <ul className="text-blue-700 text-sm mt-1 space-y-1">
                      <li>â€¢ Take photos in good lighting</li>
                      <li>â€¢ Include the entire plant in the frame</li>
                      <li>â€¢ Focus on leaves, stems, and any visible issues</li>
                      <li>â€¢ Upload photos regularly to track progress</li>
                    </ul>
                  </div>
                </div>
              </div>
            </div>
          )}

          {activeTab === 'care' && (
            <div className="space-y-6">
              <h3 className="text-lg font-semibold">Care Instructions</h3>
              
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="bg-gray-50 p-4 rounded-lg">
                  <div className="flex items-center space-x-2 mb-2">
                    <Droplets className="w-5 h-5 text-blue-600" />
                    <h4 className="font-medium">Watering</h4>
                  </div>
                  <p className="text-gray-600 text-sm">
                    Water when the top inch of soil feels dry. {plant.type} plants typically need 
                    consistent moisture but avoid overwatering.
                  </p>
                </div>

                <div className="bg-gray-50 p-4 rounded-lg">
                  <div className="flex items-center space-x-2 mb-2">
                    <Sun className="w-5 h-5 text-yellow-600" />
                    <h4 className="font-medium">Sunlight</h4>
                  </div>
                  <p className="text-gray-600 text-sm">
                    Most {plant.type} plants prefer full sun (6-8 hours daily). 
                    Ensure they receive adequate light for optimal growth.
                  </p>
                </div>

                <div className="bg-gray-50 p-4 rounded-lg">
                  <div className="flex items-center space-x-2 mb-2">
                    <Thermometer className="w-5 h-5 text-red-600" />
                    <h4 className="font-medium">Temperature</h4>
                  </div>
                  <p className="text-gray-600 text-sm">
                    Ideal temperature range: 65-75Â°F (18-24Â°C). 
                    Protect from frost and extreme temperature fluctuations.
                  </p>
                </div>

                <div className="bg-gray-50 p-4 rounded-lg">
                  <div className="flex items-center space-x-2 mb-2">
                    <Calendar className="w-5 h-5 text-green-600" />
                    <h4 className="font-medium">Fertilizing</h4>
                  </div>
                  <p className="text-gray-600 text-sm">
                    Apply organic fertilizer every 2-3 weeks during growing season. 
                    Use balanced fertilizer for healthy growth.
                  </p>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default PlantDetailView;
