import React, { useState } from 'react';
import { Search, BookOpen, Sun, Droplets, Calendar, Thermometer } from 'lucide-react';

interface PlantInfo {
  id: string;
  name: string;
  scientificName: string;
  category: 'vegetable' | 'herb' | 'fruit' | 'flower';
  difficulty: 'easy' | 'medium' | 'hard';
  sunlight: 'full' | 'partial' | 'shade';
  waterNeeds: 'low' | 'medium' | 'high';
  growingSeason: string;
  description: string;
  careTips: string[];
}

const PlantDictionary: React.FC = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState<string>('all');

  const plants: PlantInfo[] = [
    {
      id: '1',
      name: 'Tomato',
      scientificName: 'Solanum lycopersicum',
      category: 'vegetable',
      difficulty: 'medium',
      sunlight: 'full',
      waterNeeds: 'medium',
      growingSeason: 'Spring to Fall',
      description: 'A popular garden vegetable that produces red, juicy fruits. Great for beginners and experienced gardeners alike.',
      careTips: [
        'Plant in well-draining soil',
        'Provide support as they grow',
        'Water consistently but avoid overwatering',
        'Prune suckers for better fruit production'
      ]
    },
    {
      id: '2',
      name: 'Basil',
      scientificName: 'Ocimum basilicum',
      category: 'herb',
      difficulty: 'easy',
      sunlight: 'full',
      waterNeeds: 'medium',
      growingSeason: 'Spring to Fall',
      description: 'A fragrant herb that\'s perfect for cooking and easy to grow. Great companion plant for tomatoes.',
      careTips: [
        'Pinch off flower buds to encourage leaf growth',
        'Harvest regularly to promote bushiness',
        'Plant near tomatoes to deter pests',
        'Water when soil feels dry'
      ]
    },
    {
      id: '3',
      name: 'Lettuce',
      scientificName: 'Lactuca sativa',
      category: 'vegetable',
      difficulty: 'easy',
      sunlight: 'partial',
      waterNeeds: 'high',
      growingSeason: 'Spring and Fall',
      description: 'Fast-growing leafy green that\'s perfect for salads. Can be harvested multiple times.',
      careTips: [
        'Plant in cool weather',
        'Keep soil consistently moist',
        'Harvest outer leaves first',
        'Provide shade in hot weather'
      ]
    }
  ];

  const categories = [
    { id: 'all', name: 'All Plants' },
    { id: 'vegetable', name: 'Vegetables' },
    { id: 'herb', name: 'Herbs' },
    { id: 'fruit', name: 'Fruits' },
    { id: 'flower', name: 'Flowers' }
  ];

  const difficultyColors = {
    easy: 'text-green-600 bg-green-100',
    medium: 'text-yellow-600 bg-yellow-100',
    hard: 'text-red-600 bg-red-100'
  };

  const waterColors = {
    low: 'text-blue-600 bg-blue-100',
    medium: 'text-blue-600 bg-blue-100',
    high: 'text-blue-600 bg-blue-100'
  };

  const filteredPlants = plants.filter(plant => {
    const matchesSearch = plant.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         plant.scientificName.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory = selectedCategory === 'all' || plant.category === selectedCategory;
    return matchesSearch && matchesCategory;
  });

  return (
    <div className="space-y-6">
      {/* Header */}
      <div>
        <h2 className="text-3xl font-bold text-gray-900">Plant Dictionary</h2>
        <p className="text-gray-600 mt-1">Learn about different plants and their care requirements</p>
      </div>

      {/* Search and Filter */}
      <div className="flex flex-col sm:flex-row gap-4">
        <div className="flex-1 relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
          <input
            type="text"
            placeholder="Search plants..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-garden-green focus:border-transparent"
          />
        </div>
        <div className="flex gap-2">
          {categories.map((category) => (
            <button
              key={category.id}
              onClick={() => setSelectedCategory(category.id)}
              className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
                selectedCategory === category.id
                  ? 'bg-garden-green text-white'
                  : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
              }`}
            >
              {category.name}
            </button>
          ))}
        </div>
      </div>

      {/* Plants Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredPlants.map((plant) => (
          <div key={plant.id} className="bg-white rounded-xl shadow-sm border border-gray-200 p-6 hover:shadow-md transition-shadow">
            <div className="flex justify-between items-start mb-4">
              <div>
                <h3 className="text-xl font-semibold text-gray-900">{plant.name}</h3>
                <p className="text-sm text-gray-500 italic">{plant.scientificName}</p>
              </div>
              <span className={`px-2 py-1 rounded-full text-xs font-medium capitalize ${difficultyColors[plant.difficulty]}`}>
                {plant.difficulty}
              </span>
            </div>

            <p className="text-gray-600 text-sm mb-4">{plant.description}</p>

            <div className="space-y-3 mb-4">
              <div className="flex items-center space-x-2 text-sm text-gray-600">
                <Sun className="w-4 h-4" />
                <span className="capitalize">{plant.sunlight} sun</span>
              </div>
              <div className="flex items-center space-x-2 text-sm text-gray-600">
                <Droplets className="w-4 h-4" />
                <span className="capitalize">{plant.waterNeeds} water</span>
              </div>
              <div className="flex items-center space-x-2 text-sm text-gray-600">
                <Calendar className="w-4 h-4" />
                <span>{plant.growingSeason}</span>
              </div>
            </div>

            <div className="pt-4 border-t border-gray-100">
              <h4 className="font-medium text-gray-900 mb-2">Care Tips</h4>
              <ul className="space-y-1">
                {plant.careTips.slice(0, 2).map((tip, index) => (
                  <li key={index} className="text-sm text-gray-600 flex items-start">
                    <span className="text-garden-green mr-2">â€¢</span>
                    {tip}
                  </li>
                ))}
              </ul>
              {plant.careTips.length > 2 && (
                <p className="text-sm text-gray-500 mt-2">+{plant.careTips.length - 2} more tips</p>
              )}
            </div>

            <button className="w-full mt-4 bg-gray-50 hover:bg-gray-100 text-gray-700 py-2 px-3 rounded-lg text-sm transition-colors">
              View Full Details
            </button>
          </div>
        ))}
      </div>

      {/* Empty State */}
      {filteredPlants.length === 0 && (
        <div className="text-center py-12">
          <div className="w-16 h-16 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4">
            <BookOpen className="w-8 h-8 text-gray-400" />
          </div>
          <h3 className="text-lg font-medium text-gray-900 mb-2">No plants found</h3>
          <p className="text-gray-600">Try adjusting your search or filter criteria</p>
        </div>
      )}
    </div>
  );
};

export default PlantDictionary;
