import React, { useState, useEffect } from 'react';
import { Camera, Plus, Calendar, Droplets, Sun, Thermometer } from 'lucide-react';
import AddPlantModal from './AddPlantModal';
import PlantDetailView from './PlantDetailView';
import { savePlants, loadPlants } from '../utils/storage';

interface Plant {
  id: string;
  name: string;
  type: string;
  plantedDate: string;
  lastWatered: string;
  health: 'excellent' | 'good' | 'fair' | 'poor';
  image?: string;
  plantingArea?: string;
}

interface Zone {
  id: string;
  name: string;
  size: string;
}

const YourPlant: React.FC = () => {
  const [plants, setPlants] = useState<Plant[]>([]);
  const [selectedPlant, setSelectedPlant] = useState<Plant | null>(null);
  const [showAddModal, setShowAddModal] = useState(false);

  // Load plants on mount
  useEffect(() => {
    setPlants(loadPlants());
  }, []);

  // Save plants when they change
  useEffect(() => {
    savePlants(plants);
  }, [plants]);

  // Mock available zones - in a real app, this would come from PlantingArea component
  const availableZones: Zone[] = [
    { id: 'zone1', name: 'Front Yard Garden', size: '10x15 ft' },
    { id: 'zone2', name: 'Backyard Shade Area', size: '8x12 ft' }
  ];

  const healthColors = {
    excellent: 'text-green-600 bg-green-100',
    good: 'text-blue-600 bg-blue-100',
    fair: 'text-yellow-600 bg-yellow-100',
    poor: 'text-red-600 bg-red-100'
  };

  const handleAddPlant = (plantData: { name: string; type: string; plantingArea?: string }) => {
    const newPlant: Plant = {
      id: Date.now().toString(),
      name: plantData.name,
      type: plantData.type,
      plantedDate: new Date().toISOString().split('T')[0],
      lastWatered: new Date().toISOString().split('T')[0],
      health: 'good',
      plantingArea: plantData.plantingArea
    };
    setPlants([...plants, newPlant]);
  };

  const handleUpdatePlant = (plantId: string, updates: Partial<Plant>) => {
    setPlants(plants.map(plant => 
      plant.id === plantId ? { ...plant, ...updates } : plant
    ));
  };

  const handlePhotoUpload = (plantId: string) => {
    // TODO: Implement photo upload and AI analysis
    console.log('Photo upload for plant:', plantId);
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-3xl font-bold text-gray-900">Your Plants</h2>
          <p className="text-gray-600 mt-1">Track and manage your garden plants</p>
        </div>
        <button 
          onClick={() => setShowAddModal(true)}
          className="bg-garden-green hover:bg-garden-dark text-white px-4 py-2 rounded-lg flex items-center space-x-2 transition-colors"
        >
          <Plus className="w-4 h-4" />
          <span>Add New Plant</span>
        </button>
      </div>

      {/* Plants Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {plants.map((plant) => (
          <div
            key={plant.id}
            className="bg-white rounded-xl shadow-sm border border-gray-200 p-6 hover:shadow-md transition-shadow cursor-pointer"
            onClick={() => setSelectedPlant(plant)}
          >
            <div className="flex justify-between items-start mb-4">
              <div>
                <h3 className="text-xl font-semibold text-gray-900">{plant.name}</h3>
                <p className="text-gray-600">{plant.type}</p>
              </div>
              <span className={`px-2 py-1 rounded-full text-xs font-medium ${healthColors[plant.health]}`}>
                {plant.health}
              </span>
            </div>

            <div className="space-y-3">
              <div className="flex items-center space-x-2 text-sm text-gray-600">
                <Calendar className="w-4 h-4" />
                <span>Planted: {new Date(plant.plantedDate).toLocaleDateString()}</span>
              </div>
              <div className="flex items-center space-x-2 text-sm text-gray-600">
                <Droplets className="w-4 h-4" />
                <span>Last watered: {new Date(plant.lastWatered).toLocaleDateString()}</span>
              </div>
            </div>

            <div className="mt-4 pt-4 border-t border-gray-100">
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  handlePhotoUpload(plant.id);
                }}
                className="w-full bg-gray-50 hover:bg-gray-100 text-gray-700 py-2 px-3 rounded-lg flex items-center justify-center space-x-2 transition-colors"
              >
                <Camera className="w-4 h-4" />
                <span>Take Photo</span>
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* Empty State */}
      {plants.length === 0 && (
        <div className="text-center py-12">
          <div className="w-16 h-16 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4">
            <Plus className="w-8 h-8 text-gray-400" />
          </div>
          <h3 className="text-lg font-medium text-gray-900 mb-2">No plants yet</h3>
          <p className="text-gray-600 mb-4">Start by adding your first plant to track its growth</p>
          <button 
            onClick={() => setShowAddModal(true)}
            className="bg-garden-green hover:bg-garden-dark text-white px-6 py-2 rounded-lg"
          >
            Add Your First Plant
          </button>
        </div>
      )}

      {/* Add Plant Modal */}
      <AddPlantModal
        isOpen={showAddModal}
        onClose={() => setShowAddModal(false)}
        onAddPlant={handleAddPlant}
        availableZones={availableZones}
      />

      {/* Plant Detail View */}
      {selectedPlant && (
        <PlantDetailView
          plant={selectedPlant}
          onClose={() => setSelectedPlant(null)}
          onUpdatePlant={handleUpdatePlant}
        />
      )}
    </div>
  );
};

export default YourPlant;
