// Minimal localStorage utility

export const savePlants = (plants: any[]) => {
  localStorage.setItem('plants', JSON.stringify(plants));
};

export const loadPlants = () => {
  const stored = localStorage.getItem('plants');
  return stored ? JSON.parse(stored) : [];
};

export const saveZones = (zones: any[]) => {
  localStorage.setItem('zones', JSON.stringify(zones));
};

export const loadZones = () => {
  const stored = localStorage.getItem('zones');
  return stored ? JSON.parse(stored) : [];
};
