import React, { useState, useEffect } from 'react';
import { Plus, Grid, MapPin, Calendar, Sun, Droplets } from 'lucide-react';
import { areaService, AreaAnalysisResult } from '../services/areaService';
import { saveZones, loadZones } from '../utils/storage';

interface Zone {
  id: string;
  name: string;
  size: string;
  sunlight: 'full' | 'partial' | 'shade';
  soilType: string;
  plants: string[];
  image?: string;
  analysis?: AreaAnalysisResult;
}

const PlantingArea: React.FC = () => {
  const [zones, setZones] = useState<Zone[]>([]);
  const [selectedZone, setSelectedZone] = useState<Zone | null>(null);
  const [showAddModal, setShowAddModal] = useState(false);
  const [uploadingPhoto, setUploadingPhoto] = useState(false);
  const [backendStatus, setBackendStatus] = useState<'checking' | 'available' | 'unavailable'>('checking');

  // Load zones on mount
  useEffect(() => {
    setZones(loadZones());
  }, []);

  // Save zones when they change
  useEffect(() => {
    saveZones(zones);
  }, [zones]);

  const sunlightIcons = {
    full: Sun,
    partial: Sun,
    shade: Sun
  };

  const sunlightColors = {
    full: 'text-yellow-600 bg-yellow-100',
    partial: 'text-orange-600 bg-orange-100',
    shade: 'text-blue-600 bg-blue-100'
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-3xl font-bold text-gray-900">Planting Areas</h2>
          <p className="text-gray-600 mt-1">Plan and organize your garden zones</p>
        </div>
        <button className="bg-garden-green hover:bg-garden-dark text-white px-4 py-2 rounded-lg flex items-center space-x-2 transition-colors">
          <Plus className="w-4 h-4" />
          <span>Add New Zone</span>
        </button>
      </div>

      {/* Garden Zones Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {zones.map((zone) => {
          const SunIcon = sunlightIcons[zone.sunlight];
          return (
            <div
              key={zone.id}
              className="bg-white rounded-xl shadow-sm border border-gray-200 p-6 hover:shadow-md transition-shadow cursor-pointer"
              onClick={() => setSelectedZone(zone)}
            >
              <div className="flex justify-between items-start mb-4">
                <div>
                  <h3 className="text-xl font-semibold text-gray-900">{zone.name}</h3>
                  <p className="text-gray-600">{zone.size}</p>
                </div>
                <span className={`px-2 py-1 rounded-full text-xs font-medium flex items-center space-x-1 ${sunlightColors[zone.sunlight]}`}>
                  <SunIcon className="w-3 h-3" />
                  <span>{zone.sunlight}</span>
                </span>
              </div>

              <div className="space-y-3">
                <div className="flex items-center space-x-2 text-sm text-gray-600">
                  <MapPin className="w-4 h-4" />
                  <span>Soil: {zone.soilType}</span>
                </div>
                <div className="flex items-center space-x-2 text-sm text-gray-600">
                  <Grid className="w-4 h-4" />
                  <span>{zone.plants.length} plants</span>
                </div>
              </div>

              <div className="mt-4 pt-4 border-t border-gray-100">
                <div className="flex flex-wrap gap-1">
                  {zone.plants.slice(0, 3).map((plant, index) => (
                    <span key={index} className="px-2 py-1 bg-green-100 text-green-700 text-xs rounded-full">
                      {plant}
                    </span>
                  ))}
                  {zone.plants.length > 3 && (
                    <span className="px-2 py-1 bg-gray-100 text-gray-600 text-xs rounded-full">
                      +{zone.plants.length - 3} more
                    </span>
                  )}
                </div>
              </div>
            </div>
          );
        })}
      </div>

      {/* Empty State */}
      {zones.length === 0 && (
        <div className="text-center py-12">
          <div className="w-16 h-16 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4">
            <MapPin className="w-8 h-8 text-gray-400" />
          </div>
          <h3 className="text-lg font-medium text-gray-900 mb-2">No planting areas yet</h3>
          <p className="text-gray-600 mb-4">Create your first garden zone to start planning</p>
          <button className="bg-garden-green hover:bg-garden-dark text-white px-6 py-2 rounded-lg">
            Create First Zone
          </button>
        </div>
      )}

      {/* Zone Details Modal */}
      {selectedZone && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-xl max-w-lg w-full p-6">
            <div className="flex justify-between items-start mb-4">
              <h3 className="text-xl font-semibold">{selectedZone.name}</h3>
              <button
                onClick={() => setSelectedZone(null)}
                className="text-gray-400 hover:text-gray-600"
              >
                ✕
              </button>
            </div>
            
            <div className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <span className="text-gray-600 text-sm">Size</span>
                  <p className="font-medium">{selectedZone.size}</p>
                </div>
                <div>
                  <span className="text-gray-600 text-sm">Sunlight</span>
                  <p className="font-medium capitalize">{selectedZone.sunlight}</p>
                </div>
                <div>
                  <span className="text-gray-600 text-sm">Soil Type</span>
                  <p className="font-medium">{selectedZone.soilType}</p>
                </div>
                <div>
                  <span className="text-gray-600 text-sm">Plants</span>
                  <p className="font-medium">{selectedZone.plants.length}</p>
                </div>
              </div>

              <div>
                <span className="text-gray-600 text-sm">Current Plants</span>
                <div className="mt-2 flex flex-wrap gap-2">
                  {selectedZone.plants.map((plant, index) => (
                    <span key={index} className="px-3 py-1 bg-green-100 text-green-700 text-sm rounded-full">
                      {plant}
                    </span>
                  ))}
                </div>
              </div>
            </div>

            <div className="mt-6 space-y-3">
              <button className="w-full bg-garden-green hover:bg-garden-dark text-white py-2 rounded-lg">
                Edit Zone
              </button>
              <button className="w-full bg-gray-100 hover:bg-gray-200 text-gray-700 py-2 rounded-lg">
                View Layout
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default PlantingArea;
